CKEDITOR.editorConfig = function (config) {
    // Define changes to default configuration here.
    // For complete reference see:
    // https://ckeditor.com/docs/ckeditor4/latest/api/CKEDITOR_config.html

    // The toolbar groups arrangement, optimized for two toolbar rows.
    config.toolbarGroups = [
		{name: 'styles'},
		{name: 'insert'},
		{name: 'basicstyles', groups: ['basicstyles', 'cleanup']},
		{name: 'colors'},
		{name: 'paragraph',   groups: ['align', 'list', 'indent', 'blocks', 'bidi']},
/*
		{name: 'clipboard',   groups: ['clipboard', 'undo']},
*/
		{name: 'editing',     groups: ['find', 'selection', 'spellchecker']},
		{name: 'forms'},
		{name: 'links'},
		{name: 'document',    groups: ['mode', 'document', 'doctools']},
		{name: 'tools'},
		{name: 'others'},
		{name: 'about'}
    ];

    // Remove some buttons provided by the standard plugins, which are
    // not needed in the Standard(s) toolbar.
    config.removeButtons = 'Underline,Subscript,Superscript';

    // Set the most common block elements.
    config.format_tags = 'p;h1;h2;h3;pre';

    // Simplify the dialog windows.
    config.removeDialogTabs = 'image:advanced;link:advanced';
	config.extraPlugins = 'justify,colordialog,colorbutton,floatpanel,button,panelbutton,font,richcombo';

//
    config.filebrowserBrowseUrl = '/node_modules/@ckeditor/kcfinder/browse.php?opener=ckeditor&type=files&lang=tr';
    config.filebrowserImageBrowseUrl = '/node_modules/@ckeditor/kcfinder/browse.php?opener=ckeditor&type=images&lang=tr';
    config.filebrowserFlashBrowseUrl = '/node_modules/@ckeditor/kcfinder/browse.php?opener=ckeditor&type=flash&lang=tr';
    config.filebrowserUploadUrl = '/node_modules/@ckeditor/kcfinder/upload.php?opener=ckeditor&type=files&lang=tr';
    config.filebrowserImageUploadUrl = '/node_modules/@ckeditor/kcfinder/upload.php?opener=ckeditor&type=images&lang=tr';
    config.filebrowserFlashUploadUrl = '/node_modules/@ckeditor/kcfinder/upload.php?opener=ckeditor&type=flash&lang=tr';
	config.filebrowserUploadMethod = 'form';

// ...
	config.height = 500;

};
